import React, { Fragment } from 'react';
import { UserAddIcon } from '@heroicons/react/solid';
import { Field, reduxForm } from 'redux-form';
import { XCircleIcon } from '@heroicons/react/solid';

class StreamForm extends React.Component {
  renderError({ error, touched }) {
    if (touched && error) {
      return (
        <div className="p-1 mt-3 rounded-md bg-red-50">
          <div className="flex">
            <div className="flex-shrink-0">
              <XCircleIcon
                className="w-5 h-5 text-red-400"
                aria-hidden="true"
              />
            </div>
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">{error}</h3>
            </div>
          </div>
        </div>
      );
    }
  }
  renderInput = ({ input, placeholder, meta }) => {
    const className = `field ${meta.error && meta.touched ? 'bg-red-200' : ''}`;
    return (
      <div>
        <div className="mt-1 mb-6 ">
          <input
            {...input}
            type="text"
            name="email"
            id="email"
            placeholder={placeholder}
            className="block w-full pl-1 text-xl border-2 border-gray-300 rounded-md shadow-lg focus:outline-none border-gray focus:border-blue-500"
          />
          {this.renderError(meta)}
        </div>
      </div>
    );
  };

  onSubmit = (formValues) => {
    this.props.onSubmit(formValues);
  };
  render() {
    return (
      <div className="flex items-center justify-center min-h-screen px-4 py-12 bg-gray-50 sm:px-6 lg:px-8">
        <div className="w-full max-w-md space-y-8">
          <div>
            <img
              className="w-auto h-12 mx-auto"
              src="https://tailwindui.com/img/logos/workflow-mark-indigo-600.svg"
              alt="Workflow"
            />
            <h2 className="mt-6 text-3xl font-extrabold text-center text-gray-900">
              Register Your Account
            </h2>
          </div>
          <form
            className="mt-8 mb-4 space-y-6"
            onSubmit={this.props.handleSubmit(this.onSubmit)}
          >
            <Field
              name="name"
              component={this.renderInput}
              placeholder="Name"
            />
            <Field
              name="email"
              component={this.renderInput}
              placeholder="Email Address"
            />
            <Field
              name="password"
              component={this.renderInput}
              placeholder="Password"
            />
            <Field
              name="password2"
              component={this.renderInput}
              placeholder="Confirm Password"
            />
            <input type="hidden" name="remember" defaultValue="true" />
            <div className="-space-y-px rounded-md shadow-sm">
              <div>
                <label htmlFor="Name" className="sr-only">
                  Name
                </label>
                <input
                  id="Name"
                  name="Name"
                  type="Name"
                  autoComplete="Name"
                  required
                  className="relative block w-full px-3 py-2 mb-4 text-gray-900 placeholder-gray-500 border border-gray-300 rounded-none appearance-none rounded-t-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder="Name"
                />
              </div>
              <div>
                <label htmlFor="email-address" className="sr-only">
                  Email address
                </label>
                <input
                  id="email-address"
                  name="email"
                  type="email"
                  autoComplete="email"
                  required
                  className="relative block w-full px-3 py-2 mb-4 text-gray-900 placeholder-gray-500 border border-gray-300 rounded-none appearance-none focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder="Email address"
                />
              </div>
              <div>
                <label htmlFor="password" className="sr-only">
                  Password
                </label>
                <input
                  id="password"
                  name="password"
                  type="password"
                  autoComplete="current-password"
                  required
                  className="relative block w-full px-3 py-2 mb-4 text-gray-900 placeholder-gray-500 border border-gray-300 rounded-none appearance-none focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder="Password"
                />
              </div>
              <div>
                <label htmlFor="password" className="sr-only">
                  Confirm Password
                </label>
                <input
                  id="password"
                  name="password"
                  type="password"
                  autoComplete="current-password"
                  required
                  className="relative block w-full px-3 py-2 mb-4 text-gray-900 placeholder-gray-500 border border-gray-300 rounded-none appearance-none rounded-b-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder="Confrim Password"
                />
              </div>
            </div>

            <div>
              <button
                type="submit"
                className="relative flex justify-center w-full px-4 py-2 text-sm font-medium text-white bg-indigo-600 border border-transparent rounded-md group hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                <span className="absolute inset-y-0 left-0 flex items-center pl-3">
                  <UserAddIcon
                    className="w-5 h-5 text-indigo-500 group-hover:text-indigo-400"
                    aria-hidden="true"
                  />
                </span>
                Register
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

const validate = (formValues) => {
  const errors = {};
  if (!formValues.name) {
    errors.title = 'You must enter a name';
  }

  if (!formValues.email) {
    errors.description = 'You must enter an email address';
  }

  if (!formValues.password) {
    errors.title = 'You must enter a password';
  }

  if (!formValues.password2) {
    errors.description = 'You must confirm password';
  }

  return errors;
};

export default reduxForm({
  form: 'Register',
  validate
})(Register);
