class StreamForm extends React.Component {
  renderError({ error, touched }) {
    if (touched && error) {
      return (
        <div className="p-1 mt-3 rounded-md bg-red-50">
          <div className="flex">
            <div className="flex-shrink-0">
              <XCircleIcon
                className="w-5 h-5 text-red-400"
                aria-hidden="true"
              />
            </div>
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">{error}</h3>
            </div>
          </div>
        </div>
      );
    }
  }
  renderInput = ({ input, label, meta }) => {
    const className = `field ${meta.error && meta.touched ? 'bg-red-200' : ''}`;
    return (
      <div>
        <label
          htmlFor="email"
          className="block mt-2 text-sm font-medium text-gray-700"
        >
          {label}
        </label>
        <div className="mt-1 mb-6 ">
          <input
            {...input}
            type="text"
            name="email"
            id="email"
            className="block w-full pl-1 text-xl border-2 border-gray-300 rounded-md shadow-lg focus:outline-none border-gray focus:border-blue-500"
          />
          {this.renderError(meta)}
        </div>
      </div>
    );
  };

  onSubmit = (formValues) => {
    this.props.onSubmit(formValues);
  };
  render() {
    return (
      <div className="px-4 mx-auto max-w-7xl sm:px-6 lg:px-8">
        <div className="flex ">
          <form
            className="w-1/2"
            onSubmit={this.props.handleSubmit(this.onSubmit)}
          >
            <Field
              name="title"
              component={this.renderInput}
              label="enter title"
            />
            <Field
              name="description"
              component={this.renderInput}
              label="enter description"
            />
            <button className="inline-flex items-center px-4 py-2 text-sm font-medium text-indigo-700 bg-indigo-100 border border-transparent rounded-md hover:bg-indigo-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500">
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

const validate = (formValues) => {
  const errors = {};
  if (!formValues.title) {
    errors.title = 'You must enter a title';
  }

  if (!formValues.description) {
    errors.description = 'You must enter a description';
  }

  return errors;
};

export default reduxForm({
  form: 'streamForm',
  validate
})(StreamForm);
